              <Button
                size="sm"
                variant="outline"
                className="my-2"
                onClick={() => setShowContext(prev => ({ ...prev, [a.id]: !prev[a.id] }))}
              >
                {showContext[a.id] ? "Hide Agent Context" : "Show Agent Context"}
