// Purpose: Superpanel for agent patch/action queue with approve/deny, context diff, and deep audit.
//          All rationale, diff, content, and context fields are rendered with SafeMarkdown for bulletproof safety.

"use client";

import { useEffect, useState, useCallback } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { API_ROOT } from "@/lib/api";
import SafeMarkdown from "@/components/SafeMarkdown";
import { toMDString } from "@/lib/toMDString";

type ActionStatus = "pending" | "approved" | "denied";

type HistoryEntry = {
  timestamp: string;
  status: ActionStatus;
  user?: string;
  comment?: string;
};

type Action = {
  id: string;
  timestamp: string;
  status: ActionStatus;
  action: {
    type: string;
    path?: string;
    content?: string;
    diff?: string;
    context?: string;
    rationale?: string;
  };
  history?: HistoryEntry[];
};


export default function ActionQueuePanel() {
  const [actions, setActions] = useState<Action[]>([]);
  const [processing, setProcessing] = useState<string | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [showContext, setShowContext] = useState<{ [id: string]: boolean }>({});
  const [showHistory, setShowHistory] = useState<{ [id: string]: boolean }>({});
  const [comment, setComment] = useState<{ [id: string]: string }>({});
  const [compareContextId, setCompareContextId] = useState<string | null>(null);
  const [autoRefresh, setAutoRefresh] = useState(true);

  const API_KEY = process.env.NEXT_PUBLIC_API_KEY || "";
  const USER_ID = "bret";

  const fetchQueue = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const res = await fetch(`${API_ROOT}/control/list_queue`, {
        headers: {
          "X-API-Key": API_KEY,
          "X-User-Id": USER_ID
        }
      });
      if (!res.ok) throw new Error("Bad response");
      const data = await res.json();
      const mapped = (data.actions || []).map((a: Action) => ({
        ...a,
        action: {
          ...a.action,
          rationale: toMDString(a.action?.rationale),
          diff: toMDString(a.action?.diff),
          context: toMDString(a.action?.context),
          content: toMDString(a.action?.content)
        },
        history: Array.isArray(a.history)
          ? a.history.map(h => ({
              ...h,
              comment: toMDString(h.comment)
            }))
          : a.history
      }));
      setActions(mapped);
    } catch (err) {
      console.error("Queue fetch failed", err);
      setError("Failed to fetch action queue.

a.history.map(h => ({
              ...h,
              comment: toMDString(h.comment)
            }))
          : a.history
      }));
      setActions(mapped);
    } catch (err) {
      console.error("Queue fetch failed", err);
      setError("Failed to fetch action queue.");
    }
    setLoading(false);
  }, [API_KEY, USER_ID]);

  const updateStatus = async (id: string, action: "approve" | "deny") => {
    setProcessing(id + action);
    try {
      const res = await fetch(`${API_ROOT}/control/${action}_action`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "X-API-Key": API_KEY,
          "X-User-Id": USER_ID
        },
        body: JSON.stringify({ id, comment: comment[id] || "" })
      });
      if (!res.ok) throw new Error(`${action} failed`);
      await fetchQueue();
      setComment((prev) => ({ ...prev, [id]: "" }));
    } catch (err) {
      console.error(`Action ${action} failed`, err);
      setError(`Failed to ${action} action.`);
    }
    setProcessing(null);
  };

  useEffect(() => {
    fetchQueue();
    if (!autoRefresh) return;
    const interval = setInterval(fetchQueue, 15000);
    return () => clearInterval(interval);
  }, [fetchQueue, autoRefresh]);

  const getActionById = (id: string) => actions.find(a => a.id === id);

  for (const a of actions) {
    if (a.action.rationale && typeof a.action.rationale !== "string") {
      console.log("DEBUG 418:", typeof a.action.rationale, a.action.rationale);
    }
    if (a.action.diff && typeof a.action.diff !== "string") {
      console.log("DEBUG 418:", typeof a.action.diff, a.action.diff);
    }
    if (typeof a.action.content !== "string") {
      console.log("DEBUG 418:", typeof a.action.content, a.action.content);
    }
    if (a.action.context && typeof a.action.context !== "string") {
      console.log("DEBUG 418:", typeof a.action.context, a.action.context);
    }
    if (Array.isArray(a.history)) {
      for (const h of a.history) {
        if (h.comment && typeof h.comment !== "string") {
          console.log("DEBUG 418:", typeof h.comment, h.comment);
        }
      }
    }
  }

  if (error) return <p className="text-red-500">{error}</p>;
  if (loading) return <p className="text-muted-foreground">Loading queue...</p>;
  if (!actions.length) return <p className="text-muted-foreground">No actions in queue.</p>;

  return (
    <div className="space-y-4">
      <div className="flex gap-2 mb-4 items-center">
        <Button variant={autoRefresh ? "default" : "outline"} onClick={() => setAutoRefresh(!autoRefresh)}>
          {autoRefresh ? "Auto-Refresh ON" : "Auto-Refresh OFF"}
        </Button>
        <span className="text-xs text-gray-400">Queue updates every 15s</span>
      </div>
      {actions.map((a) => (
        <Card key={a.id}>
          <CardContent className="p-4 space-y-2">
            <div className="flex items-center gap-2 text-sm font-mono text-muted-foreground">
