export interface AuditLogEntry {
  id: string;
  time: string;
  source: string;
  level: string;
  message: string;
  stack_trace?: string;
  [key: string]: unknown;
}

// === Utility Export Functions =================================================
function exportJSON(logs: AuditLogEntry[]) {
  const blob = new Blob([JSON.stringify(logs, null, 2)], { type: "application/json" });
  const url = URL.createObjectURL(blob);
  const link = document.createElement("a");
  link.href = url;
  link.download = "logs_audit.json";
  link.click();
  URL.revokeObjectURL(url);
}

function exportCSV(logs: AuditLogEntry[]) {
  const headers = ["time", "source", "level", "message"];
  const csvRows = [headers.join(",")];

  logs.forEach(entry => {
    const row = [
      entry.time,
      entry.source,
      entry.level,
      (entry.message || "").toString().replace(/"/g, '""').replace(/\n/g, "\\n"),
    ].map(field => `"${field}"`);
    csvRows.push(row.join(","));
  });

  const blob = new Blob([csvRows.join("\n")], { type: "text/csv" });
  const url = URL.createObjectURL(blob);
  const link = document.createElement("a");
  link.href = url;
  link.download = "logs_audit.csv";
  link.click();
  URL.revokeObjectURL(url);
}

// === Main Component ============================================================
