        </AnimatePresence>
        {/* Navigation buttons */}
        <div className="flex justify-between mt-6">
          <Button variant="secondary" onClick={prev} disabled={step === 0}>
            Back
          </Button>
          <Button onClick={next} disabled={step === steps.length - 1}>
            {step === steps.length - 1 ? "Done" : "Next"}
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

