        raise HTTPException(404, "No matching pending action found")

    save_actions(updated)
    append_log({
        "id": action_id,
        "type": denied["action"].get("type"),
        "timestamp": datetime.utcnow().isoformat(),
        "status": "denied",
        "user": user,
        "comment": comment
    })
    return {"status": "denied"}

