from subprocess import check_output, CalledProcessError, DEVNULL

@router.get("/version")
def get_version():
    """
    Returns current Git commit short hash.
    """
    try:
        commit = check_output(["git", "rev-parse", "--short", "HEAD"], stderr=DEVNULL).decode().strip()
