  };
  history?: HistoryEntry[];
};


export default function ActionQueuePanel() {
  const [actions, setActions] = useState<Action[]>([]);
  const [processing, setProcessing] = useState<string | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [showContext, setShowContext] = useState<{ [id: string]: boolean }>({});
  const [showHistory, setShowHistory] = useState<{ [id: string]: boolean }>({});
  const [comment, setComment] = useState<{ [id: string]: string }>({});
  const [compareContextId, setCompareContextId] = useState<string | null>(null);
  const [autoRefresh, setAutoRefresh] = useState(true);

  const API_KEY = process.env.NEXT_PUBLIC_API_KEY || "";
  const USER_ID = "bret";

  const fetchQueue = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const res = await fetch(`${API_ROOT}/control/list_queue`, {
        headers: {
          "X-API-Key": API_KEY,
          "X-User-Id": USER_ID
        }
      });
      if (!res.ok) throw new Error("Bad response");
      const data = await res.json();
      const mapped = (data.actions || []).map((a: Action) => ({
        ...a,
        action: {
          ...a.action,
          rationale: toMDString(a.action?.rationale),
          diff: toMDString(a.action?.diff),
          context: toMDString(a.action?.context),
          content: toMDString(a.action?.content)
        },
        history: Array.isArray(a.history)
          ? a.history.map(h => ({
              ...h,
              comment: toMDString(h.comment)
            }))
          : a.history
      }));
      setActions(mapped);
    } catch (err) {
      console.error("Queue fetch failed", err);
      setError("Failed to fetch action queue.

a.history.map(h => ({
              ...h,
              comment: toMDString(h.comment)
            }))
          : a.history
      }));
      setActions(mapped);
    } catch (err) {
      console.error("Queue fetch failed", err);
      setError("Failed to fetch action queue.");
    }
    setLoading(false);
  }, [API_KEY, USER_ID]);

  const updateStatus = async (id: string, action: "approve" | "deny") => {
    setProcessing(id + action);
    try {
      const res = await fetch(`${API_ROOT}/control/${action}_action`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "X-API-Key": API_KEY,
          "X-User-Id": USER_ID
        },
        body: JSON.stringify({ id, comment: comment[id] || "" })
      });
      if (!res.ok) throw new Error(`${action} failed`);
      await fetchQueue();
      setComment((prev) => ({ ...prev, [id]: "" }));
    } catch (err) {
      console.error(`Action ${action} failed`, err);
      setError(`Failed to ${action} action.`);
    }
    setProcessing(null);
